pragma solidity 0.5.0;

import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";
import "./IFundableController.sol";
import "./ILoansLedger.sol";


/**
 * @title Investment Liquidity Pool.
 * @notice Pool that gather liquidity tokens to be invest on loans by a broker.
 * @author Anibal Catalán <anibalcatalanf@gmail.com>
 */
contract InvestmentLiquidityPool {
    using SafeMath for uint256;

    uint256 public liquidityPercentage;
    uint256 public feePercentage;
    uint256 public subscriptorsLimit;
    uint256 public minCap;
    uint256 public mpr;
    uint256 public ltv;
    uint256 public duration;
    uint256 public theoreticalPoolBalance;

    address private loanLedger;
    address private loanController;

    address public broker;
    address public liquidityToken;
    address[] public subscriptors;

    mapping(address => uint256) public subscriptionAmount; //subscriptor->amount
    mapping(address => uint256) public subscriptionMoment; //subscriptor->subscriptionTimestamp
    mapping(address => uint256) public subscriptorIndex; // subscriptor->arrayIndex

    mapping(address => uint256) public amountInvested; // loanContract->amount   

    constructor(
        uint256 _liquidityPercentage,
        uint256 _feePercentage,
        uint256 _subscriptorsLimit,
        uint256 _minCap,
        uint256 _mpr,
        uint256 _ltv,
        uint256 _duration,
        address _broker,
        address _liquidityToken,
        address _loanLedger,
        address _loanController
    ) public {
        require(_liquidityPercentage >= 0 && _liquidityPercentage < 100, "liquidity percentage at stock out of range");
        require(_feePercentage > 0 && _feePercentage < 10000, "fee out of range");
        require(_subscriptorsLimit > 0 && _subscriptorsLimit < 21, "number of subscriptors out of range");
        require(_mpr > 0 && _mpr < 10000, "monthly percentage rate out of range");
        require(_ltv > 0 && _ltv < 100000, "loan to value out of range");
        require(_duration >= 0 && _duration < 13, "duration out of range");
        require(_broker != address(0), "ivalid address");
        require(_liquidityToken != address(0), "invalid address");

        liquidityPercentage = _liquidityPercentage;        
        feePercentage = _feePercentage;
        subscriptorsLimit = _subscriptorsLimit;
        minCap = _minCap;
        mpr = _mpr;
        ltv = _ltv;
        duration = duration * 30 days;

        broker = _broker;
        liquidityToken = _liquidityToken;

        loanLedger = _loanLedger;
        loanController = _loanController;
    }

    function subscribe(address subscriptor, uint256 amount) public {
        require(amount <= IERC20(liquidityToken).allowance(subscriptor, address(this)), "insufficient amount of liqudiity tokens are allowed");

        if (subscriptionAmount[subscriptor] == 0) {
            require(subscriptors.length < subscriptorsLimit, "subscriptors are full");
            require(amount >= minCap, "insufficient amount");
        }

        theoreticalPoolBalance = theoreticalPoolBalance.add(amount);
        subscriptorIndex[subscriptor] = subscriptors.length;
        subscriptors.push(subscriptor);
        subscriptionMoment[subscriptor] = now;
        subscriptionAmount[subscriptor] = subscriptionAmount[subscriptor].add(amount);

        require(IERC20(subscriptor).transferFrom(subscriptor, address(this), amount), "transfer from fail");
    }

    function withdraw(uint256 amount) public {
        require(
            IERC20(liquidityToken).balanceOf(address(this)) > 
            liquidityLimit().add(amount),
            "insufficient liquidity");
        require(amount > 0 && subscriptionAmount[msg.sender] >= amount, "insufficient subscription balance");
        require(now > subscriptionMoment[msg.sender].add(duration), "can not withdraw ");

        uint256 transferAmount;

        if (subscriptionAmount[msg.sender].sub(amount) > minCap) {
            theoreticalPoolBalance = theoreticalPoolBalance.sub(amount);
            subscriptionAmount[msg.sender] = subscriptionAmount[msg.sender].sub(amount);
            transferAmount = amount;  
        } else {
            uint256 subscriptorBalance = subscriptionAmount[msg.sender];
            theoreticalPoolBalance = theoreticalPoolBalance.sub(subscriptorBalance);
            subscriptionAmount[msg.sender] = 0;
            subscriptors[subscriptorIndex[msg.sender]] = subscriptors[subscriptors.length--];
            subscriptors.length = subscriptors.length--;
            transferAmount = subscriptorBalance;
        }

        require(IERC20(liquidityToken).transfer(msg.sender, transferAmount), "transfer fail");
    }

    function invest(address loanAddress, uint128 amount) public {
        require(msg.sender == broker, "msg.sender is not the broker");
        require(IERC20(liquidityToken).balanceOf(address(this)).sub(amount) >= liquidityLimit(), "insufficient liquidity");
        require(ILoansLedger(loanLedger).isFundingContributionValid(loanAddress, address(this), amount), "invalid contribution");

        (uint256 loanAmount, uint256 installmentAmount, , , , , , , , uint8 currentState, uint8 installmentCount, ,)
        = ILoansLedger(loanLedger).getLoanData(loanAddress);

        // TODO: validate duration
        //require(installmentsCount <= duration.div(30 * days), "loan duration too long");
        require(interestRate(loanAmount, installmentAmount, installmentCount) >= mpr, "insufficient interest rate");
        // TODO: validate LTV: need collateral/USD price
        require(currentState == 4, "loan is not in funding state");

        require(IERC20(liquidityToken).approve(loanAddress, amount), "invalid approve");

        IFundableController(loanController).fund(loanAddress, 0, amount);

        amountInvested[loanAddress] = amountInvested[loanAddress].add(amount);
    }

    function claimReturns(address loanAddress) public {
        require(msg.sender == broker);

        uint128 results = ILoansLedger(loanLedger).getWithdrawAmount(loanAddress, address(this));
        require(results > 0, "nothing to claim");

        IFundableController(loanController).withdrawLenderPendingAmount(loanAddress);

        // TODO: split result by capital return and interest earns
        // TODO: send fees to broker

        distributeResults(results);
    }

    function balanceOf(address subscriptor) public view returns (uint256) {
        return subscriptionAmount[subscriptor];
    }

    function showSubscriptors() public view returns(address[] memory) {
        return subscriptors;
    }

    function liquidityLimit() public view returns(uint256) {
        return (theoreticalPoolBalance.mul(liquidityPercentage).div(100));
    }

    function poolData() public view returns (
        uint256,
        uint256,
        uint256,
        uint256,
        uint256,
        uint256,
        uint256,
        uint256,
        uint256,
        uint256,
        address) 
    {
        uint256 numOfSubscriptors = subscriptors.length;
        return ( 
        theoreticalPoolBalance,
        IERC20(liquidityToken).balanceOf(address(this)),
        liquidityPercentage, 
        numOfSubscriptors,
        subscriptorsLimit,
        minCap,
        mpr,
        ltv,
        duration,
        feePercentage,
        broker);
    }

    function distributeResults(uint256 results) internal {
        uint256 amountSplitted;
        for (uint8 i; i < subscriptorsLimit; i++) {
            uint256 earns = subscriptionAmount[subscriptors[i]].mul(100).mul(results).div(theoreticalPoolBalance);
            amountSplitted = amountSplitted.add(earns);
            subscriptionAmount[subscriptors[i]] = subscriptionAmount[subscriptors[i]].add(earns);
        }
        if (amountSplitted < results) {
            subscriptionAmount[subscriptors[0]] = subscriptionAmount[subscriptors[0]].add(results.sub(amountSplitted));
        }
    }

    function interestRate(uint256 loanAmount, uint256 installmentAmount, uint256 installmentCount) internal pure returns (uint256) {
        uint256 a = installmentCount.mul(installmentCount).sub(1).mul(loanAmount);
        uint256 b = installmentCount.add(1).mul(loanAmount).mul(6);
        uint256 c = installmentAmount.mul(installmentCount).sub(loanAmount).mul(12);

        return sqrt(b.mul(b).add((a).mul(c).mul(4))).sub(b).mul(10000).div(a.mul(2));
    }

    function sqrt(uint256 x) internal pure returns (uint256 y) {
        uint256 z = (x + 1) / 2;
        y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
    }

}

